import { LOAD_JOB,LOAD_PARA,INITIALIZE,CLEARDATA, SETLANGUAGE } from "../actions/type";

import { useCookies } from 'react-cookie';
import globalconst from "../constant/globalvariables";

//const user = null;
const initialState={languageList:[]}

 const fetchReducer= (state, action) => {
  const { type, payload } = action;
  
switch (type) {
  case CLEARDATA:
    return{
      ...state, 
      results:payload.results,
     // fetchpayload:payload
    };
  case SETLANGUAGE:
    alert("set in fetch reducer"+JSON.stringify(state));
    return{
      ...state, 
      //testElement:{...state.testElement,results:payload.results},
      selectedObject:{...state.selectedObject,results:[...new Set(state.selectedObject.results,payload.results)]},
      
      languageList:[...state.languageList.filter((lan)=>(!(lan.lanIndex==state.selectedObject.selectedLanguage && lan.workType==state.selectedObject.workType) )),{...state.selectedObject,results:[...new Set(state.selectedObject.results,payload.results)]}],
      trialObject:{[state.selectedWorkType]:{[state.selectedLanguage]:{results:[...new Set(state.selectedObject.results,payload.results)]}}},
      finalObject:{...state.finalObject,[state.selectedWorkType]:{[state.selectedLanguage]:{results:[...new Set(state.selectedObject.results,payload.results)]}}}
      //selectedWorkType:state.language.selectedWorkType,
      //selectedLanguageIndex:state.language.selectedLanguageIndex,
    };
  case LOAD_PARA:
      return {
        ...state,
        results:[...new Set([payload.results,state.results])],
        selectedWorkType:state.language.selectedWorkType,
      selectedLanguageIndex:state.language.selectedLanguageIndex,
      };
      /*
  case INITIALIZE:
    
    alert("initialize "+JSON.stringify(payload));
    const languages=formLanguages(payload);
    alert("languages "+JSON.stringify(languages))
      return {
        ...state,
        languages:languages,
      };
      */
    default:
      return state;
  }
};
