import { SETWORKTYPE } from "../actions/type";
import fetchReducer from "./fetch";
import { combineReducers, configureStore } from '@reduxjs/toolkit'
import languageReducer from "./languages";

//const user = null;
//const initialState={selectedWorkType:"Pending"}
 const dataReducer= (state, action) => {
  const { type, payload } = action;
  
switch (type) {
  case SETWORKTYPE:
    return{
      ...state, 
      selectedLanguage:[payload.language],
      
    };
    default:
      return state;
  }
};

function workTypeReducer(state, action) {
    const intermediateState =fetchReducer (state, action)
    const finalState =dataReducer(intermediateState, action)
    return finalState
  }



export default workTypeReducer
