                      }*/
                    }
    case SETWORKTYPE:
      return{
        ...state,
       // selectedLanguage:payload.selectedLanguage,
        selectedWorkType:payload.selectedWorkType,
      [payload.selectedLanguage.key]:{
        ...state[payload.selectedLanguage.key]
        
        }
                    }
                    
    case LOAD_PARA:
      return{
        ...state,
       // selectedLanguage:payload.selectedLanguage,
       // selectedWorkType:payload.selectedWorkType,
       [payload.selectedLanguage.key]:{
        ...state[payload.selectedLanguage.key]
        
        }
                    
                      
       //selectedObject:{...state.selectedObject,results:['a','b','c']}
      }
    case LOGIN_SUCCESS:
                  return {
                    ...state,
                    selectedLanguage:payload.user.languages[0],
                    selectedWorkType:"Pending",

                    [payload.user.languages[0].key]:{["Pending"]:{results:[],nextUrl:null,prevUrl:null}}
                  }
                
                  
    default:
      return state;
  }
};
  /*case ONLOAD:
    return{
      ...state,
