import React from "react";
import "react-native-gesture-handler";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
const Stack = createNativeStackNavigator();
import Home from "./../screens/Home/index";
import Login from "./../screens/Login/index";
import { useCookies } from 'react-cookie';
import axios from "axios";
import globalconst from "../constant/globalvariables";
import { useDispatch ,useSelector} from "react-redux";
import { onLoad } from "./../actions/auth";
import { useState } from "react";

const AuthStack = (props) => (
  <NavigationContainer>
    <Stack.Navigator initialRouteName={props.initialRouteName}>
      <Stack.Screen
        name="LoginScreen"
        component={Login}
        options={{ headerShown: false }}
        {...props}
      />
      <Stack.Screen
        name="HomeScreen"
        component={Home}
        options={{ headerShown: false }}
        {...props}
      />
    </Stack.Navigator>
  </NavigationContainer>
);
const onLoad1=()=>{
  const state = useSelector((state) => state);
  const dispatch = useDispatch();
  alert("before"+state.user);
  dispatch(onLoad())
  .then((response)=>{
    alert("after"+state.user);
    alert("from navigation "+JSON.stringify(response))
  })
  .catch((error)=>{

  })
 // setInitialCall(false);

};

const NavigationProvider = (props) => {
  const dispatch = useDispatch();
 // const user = useSelector((state) => state.user);
  const [cookies, setCookie] = useCookies(['bhaashadan']);
  // var initialCall=true;
  //if(initialCall){
  var initialRouteName='LoginScreen';
  //var param={}
  
  axios.defaults.baseURL = globalconst.apiBaseUrl;
  
  if(cookies.bhaashadan!='undefined' && cookies.bhaashadan!=null && cookies.bhaashadan.user!=null)
  {
    initialRouteName='HomeScreen';
    const user=cookies.bhaashadan.user;
    axios.defaults.headers.common.Authorization=cookies.bhaashadan.token;
   // param={...props,user:user}
  }
  //initialCall=false;
  
  if(cookies.bhaashadan.user && !user)
  {
    dispatch(onLoad(cookies.bhaashadan.user));
   // setUser()
  }

  
  
  
  
  return <AuthStack initialRouteName={initialRouteName}/>;
};
export default NavigationProvider;
