import { StatusBar } from 'expo-status-bar';
import React,{useLayoutEffect,useState} from 'react';
import react, { Component } from 'react';
import { StyleSheet, Text, View,TouchableOpacity,Modal, FlatList, ActivityIndicator,SafeAreaView,Dimensions} from 'react-native';
import axios from "axios";
//import { useInfiniteQuery } from 'react-query';

import CardFrame from './cardFrame';
import Styles from '../constant/GlobalStyles'
import Header from './Header';
import Profile from './Profile';
import ButtonTypeRadio from './ButtonTypeRadio';
import ModalView from './ModalView';


import Colors from '../constant/color'
import { useDispatch, useSelector } from 'react-redux';
import { getParagraph } from '../actions/fetch';

const FlatListView=(props)=>{
    //const state1 = useSelector((state) => state);
   // const state = props.stateObject;
    //const state=state1.selectedObject;
//    alert("state in flatlist"+JSON.stringify(state));
const dispatch=useDispatch();
let currentObj=props.stateObject.selectedObject;
const [refreshing,setRefreshing]=useState(false);

//var selectedItem=null;
function _renderItem(item){
  // alert(item);
  return (<CardFrame {...item} key={item.id} workType={currentObj.selectedWorkType}/>)
}
function handleScroll() {
alert(JSON.stringify(currentObj));
  if(!refreshing){
   // dispatch(getParagraph(currentObj))
    dispatch(getParagraph({selectedLanguage:currentObj.selectedLanguage,selectedWorkType:currentObj.selectedWorkType,alreadyExists:true,nextUrl:currentObj.nextUrl}))
    setRefreshing(true);
  }
  else{
   // setRefreshing(false);
  }    
};
    return(
      <View>
        <FlatList
                    //ref={this.flatListRef}
                    keyExtractor={(item) => item.id}
                    key={(item)=>item.id}
                    contentContainerStyle={{ justifyContent: 'center', flexDirection: 'row', flexWrap: 'wrap' }}
                    data={currentObj.results}
                    renderItem={(item,props)=>_renderItem(item)}
                 //  renderItem={(item)=><View><Text>{item.id}</Text></View>}
                   ListEmptyComponent={<View><Text> no item to render</Text></View>}
                   hasMore={currentObj.nextUrl?true:false}
                   //prevUrl  nextUrl
                   onEndReached={
                    currentObj.nextUrl?
                     handleScroll()
                    :
                    ""
                   }
                   onEndReachedThreshold={
                    currentObj.nextUrl?
                    0.2
                    :
                    0
                    }

                    refreshing={refreshing}
                    horizontal={false}
                    //onEndReached={this.handleScroll.bind(this)}
                   // refreshing={this.state.refreshing}
                 //  getItemLayout={(any,number) => ( {length: 200,offset:(250*((number-1)/numberOfColumn)), index: number})}
                    //initialScrollIndex={0}
                  //  initialScrollIndex={this.state.itemToRender[this.state.workType][this.state.selectedLanguage.key].scrollIndex}
                    //scrollEnabled={true}
                   //onScroll={handleScroll()}
                   // maxToRenderPerBatch={10}
                   // numColumns={2}
                   // style={{width:'100%',flexGrow:1}}
                    //onMomentumScrollBegin={() => {this.onEndReachedCalledDuringMomentum = false;}}
                    />


                    </View>
    )
};

export default FlatListView
