import { LOAD_PARA,LOAD_JOB,INITIALIZE,SETLANGUAGE, SETWORKTYPE } from "./type";
import DataFetchService from "../services/dataFetchService";
export const initialize=(languages)=>(dispatch)=>{
    dispatch({
        type: INITIALIZE,
        payload: languages 
      })
}

