import React, { useState} from "react";
import { View, Text, StyleSheet, Button,TouchableOpacity,ActivityIndicator,FlatList,Modal } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../../actions/auth";
import { useCookies } from "react-cookie";
import { withCookies, Cookies } from 'react-cookie';
import { initialize } from "../../actions/fetch";
import { clearAllStoreData } from "../../actions/rootAction";
import Colors from "../../constant/color"
import Styles from "../../constant/GlobalStyles"
import ButtonTypeRadio from "../../customComponent/ButtonTypeRadio"
import { setSelectedLanguage,setWorkType,getParagraph } from "../../actions/fetch";
import languageReducer from "../../reducers/languages";
import CardFrame from "../../customComponent/cardFrame";
import { useEffect } from "react";
import FlatListView from "../../customComponent/FlatLIstView";
import Header from "../../customComponent/Header";
import Profile from "../../customComponent/Profile";
import Logo from "../../customComponent/logo";
import globalconst from "../../constant/globalvariables";
import ModalView from '../../customComponent/ModalView';

const Home = (props) => {
  //const [cookies, setCookie] = useCookies();
  const stateObj = useSelector((state) => state=>language);
  const authObj = useSelector((state) => state=>auth);
  //const [workType,setWorkType]=useState("Pending");
  const [isLoading,setIsLoading]=useState(false);
  
  const dispatch = useDispatch();
  
  const { navigation }=props;

  
  //alert("reduce "+JSON.stringify(stateObj.selectedObject.nextUrl));
  var first_name='';
  /*
  if(authObj && authObj.user)
  first_name=authObj.user.first_name;
*/

  
const onLogout = (props) => {
  const {cookies}=props;
  //alert("home cookie"+JSON.stringify(cookies));
  
    dispatch(clearAllStoreData())

        navigation.replace("LoginScreen");
 
  };

  function onSetLanguage(state,item){
    //const dispatch = useDispatch();
    {
      if(item.key!=stateObj.selectedLanguage.key) {
        var alreadyExists=false;
        var obj=null;
      const apiURL=globalconst.apiBaseUrl+"/api/paragraph"+"?language="+item.key+"&page=1";
      obj=getAlreadyExist(state,item.key,stateObj.selectedWorkType)
      if(obj.length>0){
        alreadyExists:true;
      apiURL=alreadyExists[0].nextUrl;
      }
        
      dispatch(setSelectedLanguage({selectedLanguage:item,selectedWorkType:stateObj.selectedWorkType,alreadyExists:alreadyExists,url:apiURL}))
      }
    }
  };
  function getAlreadyExist(state,selectedLanguage,selectedWorkType){
    var obj=stateObj.listOfObject.filter((obj)=>(obj.selectedLanguage.key==selectedLanguage.key && obj.selectedWorkType==selectedWorkType));
  //  alert("obj"+obj);
    //if(obj.length>0)
    return obj;
 // return false;
  } ;
  function onSetWorkType(state,item){
   // const dispatch = useDispatch();
    {
    if(stateObj.workType!=item) {
      var obj=null;
      var alreadyExists=false;
      var workType="job";
      if(item=="Pending")
      workType="paragraph"
      
      const apiURL=globalconst.apiBaseUrl+"/api/"+workType+"?language="+stateObj.selectedLanguage+"&page=1";
      obj=getAlreadyExist(state,stateObj.selectedLanguage.key,item);
      if(obj.length>0)
      {
        alreadyExists:true;
        apiURL=alreadyExists[0].nextUrl;
      }

      
      dispatch(setWorkType({selectedLanguage:stateObj.selectedLanguage,selectedWorkType:item,alreadyExists:alreadyExists,url:apiURL}))
    }  
  }
};
 
return (
  
  <View style={Styles.container}>
    
    
     
      <View style={localStyles.container}>      
      <Header showSearch="false"/>
      
      <View style={localStyles.middleContainer}>
      <Logo/>
      </View>
      
      <View style={localStyles.lastContainer}>
          <View style={localStyles.Profile}>
              <Profile user={authObj.user} {...props}/>
          </View>
          <View style={localStyles.content}>
          
              <View style={localStyles.lanConatiner}>
                  <View style={{alignContent:'flex-start',justifyContent:'flex-start',flexDirection:'row',minWidth:'120px',flexWrap:'wrap'}}>
                       { authObj.user.languages.map((item,index) => 
                      <ButtonTypeRadio 
                      key={item.key}
                     style={item.key==stateObj.selectedLanguage.key?[{...localStyles.activeButtonStyle,...localStyles.activeWorkType}]:[{...localStyles.buttonStyle,...localStyles.passiveWorkType}]} 
                     item={item} 
                     handleClick={()=>{onSetLanguage(state,item)}}
                      />)}
                  </View>
                  <View style={{alignContent:'flex-end', justifyContent:'flex-end',flexDirection:'row',minWidth:'80px'}}>
                  <TouchableOpacity onPress={()=>{onSetWorkType(state,"Uploaded")} }>
                        <Text style={[{...localStyles.toggleFirstHalf},stateObj.selectedWorkType!="Uploaded"?{...localStyles.passiveWorkType}:{...localStyles.activeWorkType}]}>Uploaded</Text></TouchableOpacity>
                      <TouchableOpacity onPress={()=>{onSetWorkType(state,"Pending")} }>
                        <Text style={[{...localStyles.toggleSecondHalf},stateObj.selectedWorkType!="Pending"?{...localStyles.passiveWorkType}:{...localStyles.activeWorkType}]}>Pending</Text></TouchableOpacity>
                  </View>
              
              </View>
              <View style={localStyles.tileContainer}>
              {isLoading?
              <View>
                  <ActivityIndicator size='large'/>

              </View>
              
              :
              <View>
              
           <FlatListView stateObject={stateObj} />
           </View>   }        
                </View> 
                </View>
                </View>  
                </View>
  
  
    
     
  
    </View>

    
  );
};
  

 
//export default withCookies(Home);
export default Home


const localStyles= StyleSheet.create({
  header:{
      backgroundColor:'#0eca7e',
      justifyContent:'center',
      //height:'10%',
      flex:1,
      width:'100%', 
     // flexGrow:1,
     minwidth:'380px',
  },
  middleContainer:{
      backgroundColor:'#0aae6c',
      justifyContent:'center',
     // height:'15%',
     flex:1.5,
      width:'100%',
      minwidth:'380px',
  },
  lastContainer:{
      flexDirection:'row',
      backgroundColor:'#e3eaea',
      justifyContent:'center',
     // height:'35%',
     flex:8.5,
      width:'100%',
      minWidth:'380px'
     // flexWrap:'wrap',
      
  },
  Profile:{
      top:'-15%',
     // width:'30%',
      height:'100%',
      flex:3,
      margin:5,
      padding:5,       
      alignItems:'center',
      alignContent:'center',
      flexDirection:'column',
      zIndex:1,
      minWidth:'120px',
      overflow:'auto',
  },
  container: {
      //height:'100%',
      width:'100%',

      flex:10,
      flexDirection:'column',
      backgroundColor: '#0eca7e',
      alignItems: 'center',
      justifyContent: 'flex-start',
      //flexWrap:'nowrap',
     // overflow:'auto',
      zIndex:0,
      minWidth:'380px'
    },
    content:{
       //height:height,
      //width:'65%',
      minWidth:'200px',
      height:'100%',
      flex:7,
       flexDirection:'column',
       backgroundColor: '#e3eaea',
       alignItems: 'flex-start',
       justifyContent: 'center',
       alignContent:'flex-start',
       //flexWrap:'wrap',
      // overflow:'auto',
       // top:100,
     },
    tileContainer:{ 
      //height:height,
      flex:9,
      width:'100%',
      height:'80%',
      flexDirection:'column',
      backgroundColor: '#e3eaea',
      alignItems: 'flex-start',
      justifyContent: 'flex-start',
      alignContent:'flex-start',
      //flexWrap:'wrap',
      //overflow:'auto',
      // top:100,
    },
    lanConatiner:{ 
      //flex:1,
      width:'100%',
     // width:'200px',
      flexDirection:'row',
      backgroundColor: '#e3eaea',
      justifyContent: 'space-between',
     // justifyContent: '//#endregion',
     // alignContent:'center',
     // flexWrap:'wrap',
      overflow:'auto',
     // top:100,
    },
    activeButtonStyle:{
      backgroundColor:Colors.dashboardRow2Color,
      borderColor:Colors.dashboardRow2Color,
      borderWidth:1,
      borderRadius:10,
      margin:5,
      height:20,
      //fontSize:10,
      paddingHorizontal:2,
    },
    
    buttonStyle:{
      backgroundColor:Colors.dashboardRow1Color,
      borderColor:Colors.dashboardRow2Color,
      borderWidth:1,
      borderRadius:10,
      margin:5,
      height:20,
      //fontSize:10,
      paddingHorizontal:2,
    },
    toggleFirstHalf:{
      backgroundColor:Colors.dashboardRow2Color,
      borderBottomStartRadius:10,
      borderTopStartRadius:10,
      borderBottomEndRadius:0,
      borderTopEndRadius:0,
      paddingHorizontal:2,
      marginVertical:5,
      height:20,
      //fontSize:10,
    },
    toggleSecondHalf:{
      backgroundColor:Colors.dashboardRow2Color,
      borderBottomStartRadius:0,
      borderTopStartRadius:0,
      borderBottomEndRadius:10,
      borderTopEndRadius:10,
      paddingHorizontal:2,
      //marginTop:5,
      marginVertical:5,
      height:20,
     // fontSize:10,
    },
    passiveWorkType:{
      fontFamily:'Roboto',
      fontSize:'14px',
      fontWeight:'500',
      fontStyle:'normal',
      lineHeight:'20px',
      backgroundColor:'white',
    },
    activeWorkType:{
      fontFamily:'Roboto',
      fontSize:'14px',
      fontWeight:'500',
      fontStyle:'normal',
      lineHeight:'20px',
    }
});
