import React from "react";
import { View, Text, StyleSheet, Button } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "./../../actions/auth";
import { useCookies } from "react-cookie";
import { withCookies, Cookies } from 'react-cookie';
import { initialize } from "../../actions/fetch";

const Home = (props) => {
  const { navigation }=props;
  const dispatch = useDispatch();
  const [cookies, setCookie] = useCookies();
  const state = useSelector((state) => state);
  var first_name='';
  if(state && state.user)
  first_name=state.user.first_name;

  
const onLogout = (props) => {
  const {cookies}=props;
  alert("home cookie"+JSON.stringify(cookies));
  cookies.remove('bhaashadan');
    dispatch(logout()).then((response) => {
      if (response.status === "success") {
        navigation.replace("LoginScreen");
      }
    });
  };
  function initializeLanguages(user){
    dispatch(initialize()).then((response) => {
      alert("initialise"+JSON.stringify(response));
     // if (response.status === "success") {
       // navigation.replace("LoginScreen");
     // }
    });

  };
  initializeLanguages(state.user);
return (
  
  <View style={Styles.container}>
    {
      (state.user)?
    <>
      <Text style={{ fontSize: 16 }}>Welcome {JSON.stringify(state.user)}  {JSON.stringify(state.languages)}</Text>
      <Button onPress={() => onLogout(props)} title="Logout" />
      </>
  
  :
  <>No User found</>
    }
    </View>
  );
};
export default withCookies(Home);
const Styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});
