import React from "react";
import "react-native-gesture-handler";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
const Stack = createNativeStackNavigator();
import UserDashboard from "./../screens/Home/UserDashboard";
import Login from "./../screens/Login/Login";
import Home from "./../screens/Login/Home";
import Register from "./../screens/Login/Register";
import { useCookies } from 'react-cookie';
import axios from "axios";
import globalconst from "../constant/globalvariables";
import { useDispatch ,useSelector} from "react-redux";
import { onLoad } from "./../actions/auth";
import { initialize } from "../actions/fetch";
import { useState } from "react";


var initialRouteName='Home';
const AuthStack = (props) => (
  
    <Stack.Navigator initialRouteName={props.initialRouteName}>
      <Stack.Screen
        name="Login"
        component={Login}
        options={{ headerShown: false }}
        {...props}
      />
      <Stack.Screen
        name="Home"
        component={Home}
        options={{ headerShown: false }}
        {...props}
      />
      <Stack.Screen
        name="Register"
        component={Register}
        options={{ headerShown: false }}
        {...props}
      />
    </Stack.Navigator>
  
);
const UserStack = (props) => (
  
    <Stack.Navigator initialRouteName={props.initialRouteName}>
      <Stack.Screen
        name="UserDashboard"
        component={UserDashboard}
        options={{ headerShown: false }}
        {...props}
      />
    </Stack.Navigator>
  
);

const handleCookie=(props)=>{
  const dispatch = useDispatch();
   const [cookies, setCookie] = useCookies(['bhaashadan']);
   axios.defaults.baseURL = globalconst.apiBaseUrl;
   if(cookies.bhaashadan!='undefined' && cookies.bhaashadan!=null && cookies.bhaashadan.user!=null)
   {
     initialRouteName='UserDashboard';
     axios.defaults.headers.common.Authorization=cookies.bhaashadan.token;
   }
   if(cookies && cookies.bhaashadan && cookies.bhaashadan.user)
   {
     dispatch(onLoad(cookies.bhaashadan.user)).then((response)=>{
     // alert("onload "+JSON.stringify(response));
     // dispatch(initialize(response.bhaashadan.user));
     navigation.replace("User",{screen:"UserDashboard"});
     });
   }
   else
   navigation.replace("Auth",{screen:"Login"});;
}
const NavigationProvider = (props) => {
  <NavigationContainer>
    <Stack.Navigator initialRouteName={props.initialRouteName}>

    <Stack.Screen
          name="Auth"
          component={AuthStack}
          options={{ headerShown: false }}
        />
<Stack.Screen
          name="User"
          component={UserStack}
          options={{ headerShown: false }}
        />
</Stack.Navigator>
</NavigationContainer>
};
export default NavigationProvider;
