import React from "react";
import { View, Text, StyleSheet, Button } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "./../../actions/auth";
import { useCookies } from "react-cookie";
import { withCookies, Cookies } from 'react-cookie';
import { initialize } from "../../actions/fetch";
import { clearAllStoreData } from "../../actions/rootAction";

const Home = (props) => {
  const { navigation }=props;
  const dispatch = useDispatch();
  const [cookies, setCookie] = useCookies();
  const state = useSelector((state) => state);
  var first_name='';
  if(state && state.auth && state.auth.user)
  first_name=state.auth.user.first_name;

  
const onLogout = (props) => {
  const {cookies}=props;
  alert("home cookie"+JSON.stringify(cookies));
  
    dispatch(clearAllStoreData()).then((response) => {
      //if (response.status === "success") {
       // cookies.remove('bhaashadan');
        navigation.replace("LoginScreen");
        
     // }
    });
  };
 
return (
  
  <View style={Styles.container}>
    {
      (state.auth.user)?
    <>
      <Text style={{ fontSize: 16 }}>Welcome {JSON.stringify(state.auth.user)}  {JSON.stringify(state.fetch.languages)} </Text>
      <Button onPress={() => onLogout(props)} title="Logout" />
      </>
  
  :
  <>No User found</>
    }
    </View>
  );
};
export default withCookies(Home);
const Styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});
