import { useCookies } from 'react-cookie';
import globalconst from "../constant/globalvariables";

//const user = null;
//const initialState={results:[]}

 const fetchReducer= (state, action) => {
  const { type, payload } = action;
  
switch (type) {
  case CLEARDATA:
    return{
      ...state, 
      results:[],
    };
  case SETLANGUAGE:
    alert("set in fetch reducer"+JSON.stringify(state));
    return{
      ...payload, 
      results:[],
      
      //selectedWorkType:state.language.selectedWorkType,
      //selectedLanguageIndex:state.language.selectedLanguageIndex,
    };
  case LOAD_PARA:
      return {
        ...state,
        results:[...new Set([payload.results,state.results])],
        selectedWorkType:state.language.selectedWorkType,
      selectedLanguageIndex:state.language.selectedLanguageIndex,
      };
      /*
  case INITIALIZE:
    
    alert("initialize "+JSON.stringify(payload));
    const languages=formLanguages(payload);
    alert("languages "+JSON.stringify(languages))
      return {
        ...state,
        languages:languages,
      };
      */
    default:
      return state;
  }
};
