  //alert(values['username']);
  //alert(values['password']);
  var errorMsg=null;
  //alert(values['rememberMe']);
  const endpoint ='api/auth/';
    //alert(endpoint);
    const payload = { username: values['username'], password: values['password'] } ;
    //alert("input username="+values['username']);
    axios.defaults.baseURL = 'http://bhaasha.iiit.ac.in/crowd';
    axios.defaults.headers.post['Content-Type'] ='application/x-www-form-urlencoded';
    const rememberMe=values['rememberMe'];
    
	//alert(" rememberMe")
	// let self=props;
    return axios({
        method: 'post',
        url: endpoint,
        data: payload
      })
      
      .then(function (response) {
        
      //  Promise.resolve(response);
       return response
            
         
      })
      .catch(function (error) {
       // alert(error.response.status);
        if(error.response.data.non_field_errors!=null)
        {
        alert("API call fails with error:"+error.response.data.non_field_errors);
        console.log(error.response.data.non_field_errors);
        }
        else{
        alert("API call fails with error:"+error);
        console.log(error);
        }
        return error;
        
      });
      
}
const logOut = async () => {
  AsyncStorage.clear();
  return {
    status: "success",
    message: "You are logged out",
  };
};
export default {
  logIn,
  logOut,
};
