import { SETLANGUAGE, SETWORKTYPE } from "../actions/type";
import fetchReducer from "./fetch";
import { combineReducers, configureStore } from '@reduxjs/toolkit'
import languageReducer from "./languages";

//const user = null;
const initialState={lanIndex:0,workType:"Pending",results:[]}
 const dataReducer= (state=initialState, action) => {
  const { type, payload } = action;
  
switch (type) {
  case SETLANGUAGE:
    alert("state in worktype reducer "+JSON.stringify(state));
    return{
     // ...state, 
      //languagelanIndex:payload.lanIndex,
     // workType:payload.workType,
      languageList:[...state.languageList.filter((lan)=>(!(lan.lanIndex==payload.lanIndex && lan.workType==payload.workType) )),{lanIndex:state.lanIndex,workType:state.workType,results:state.results}],

      
      
     // results:state.results,
     // results:null
      //results:new Set(payload.results)
    };
    default:
      return state;
  }
};

function workTypeReducer(state, action) {
    const intermediateState =dataReducer(state, action)
    const finalState =fetchReducer(intermediateState, action)
    return finalState
  }



export default workTypeReducer
