import { LOGIN_SUCCESS, LOGOUT,ONLOAD } from "../actions/type";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useCookies } from 'react-cookie';
import globalconst from "../constant/globalvariables";
const initialState = user
  ? { isLoggedIn: true, user:user }
  : { isLoggedIn: false, user: null };

const user = null;
const formLanguages=(payload)=>{
  if(payload && payload.user){
      const user=payload.user;
      const lan=user.languages;
      if(lan){
        var languages=[];
        
        for(var i=0;i<lan.length;i++)
        {
          var key=lan[i].key;
          var item=lan[i].item;
          if(key==="bengali"){
            key="bangla";
            item="Bangla";
          }
          var tempObj={
            id:key,
            title:item,
            Uploaded:{
            isPrevApiCalled:false,
            count:-1,
            items:[],
            pagenumber:0,
            lastPageNumber:-1,
            nextPage:globalconst.apiBaseUrl+'api/job?language='+key+'&page=1',
            scrollIndex:0,
            hasMore:true
            }
            ,
            Pending:{
            isPrevApiCalled:false,
            count:-1,
            items:[],
            pagenumber:0,
            lastPageNumber:-1,
            nextPage:globalconst.apiBaseUrl+'api/paragraph?language='+key+'&page=1',
            scrollIndex:0,
            hasMore:true
            }
            
            }
          
          languages.push(tempObj);

        }
        return languages;
      }

  }
  return null;
}

 const auth= (state = initialState, action) => {
  const { type, payload } = action;
 // const languages=formLanguages(payload);
switch (type) {
  case ONLOAD:
    return{
      ...state,
      isLoggedIn: payload.user?true:false,
      user: payload.user,  
    //  languages:languages,
    };
  case LOGIN_SUCCESS:
      return {
        ...state,
        isLoggedIn: true,
        user: payload.user,
    //    languages:languages,
      };
  case LOGOUT:
      return {
        ...state,
        isLoggedIn: false,
        user: null,
      };
    default:
      return state;
  }
};
export default auth
