import { SETLANGUAGE } from "../actions/type";
import workTypeReducer from "./worktype";

//const user = null;
const dataReducer = combineReducers({
  workTypeReducer:workTypeReducer,
  
});

 const languageReducer= (state, action) => {
  const { type, payload } = action;
  
switch (type) {
  case SETLANGUAGE:
    return{
      ...state, 
      selectedLanguageIndex:payload.index,
    };
    default:
      return dataReducer;
  }
};
export default languageReducer
