import React from "react";
import "react-native-gesture-handler";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
const Stack = createNativeStackNavigator();
import Home from "./../screens/Home/index";
import Login from "./../screens/Login/index";
import { useCookies } from 'react-cookie';
import axios from "axios";
import globalconst from "../constant/globalvariables";
import { useDispatch ,useSelector} from "react-redux";
import { onLoad } from "./../actions/auth";
import { initialize } from "../actions/fetch";
import { useState } from "react";


var initialRouteName='LoginScreen';
const AuthStack = (props) => (
  <NavigationContainer>
    <Stack.Navigator initialRouteName={props.initialRouteName}>
      <Stack.Screen
        name="LoginScreen"
        component={Login}
        options={{ headerShown: false }}
        {...props}
      />
      <Stack.Screen
        name="HomeScreen"
        component={Home}
        options={{ headerShown: false }}
        {...props}
      />
    </Stack.Navigator>
  </NavigationContainer>
);
