import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { login } from "../actions/auth";
import { useCookies } from 'react-cookie';
const logIn = async (values) => {
  
  //alert(values['username']);
  //alert(values['password']);
  var errorMsg=null;
  //alert(values['rememberMe']);
  const endpoint ='api/auth/';
    //alert(endpoint);
    const payload = { username: values['username'], password: values['password'] } ;
    //alert("input username="+values['username']);
    axios.defaults.baseURL = 'http://bhaasha.iiit.ac.in/crowd';
    axios.defaults.headers.post['Content-Type'] ='application/x-www-form-urlencoded';
    const rememberMe=values['rememberMe'];
    
	alert(" rememberMe"+rememberMe);
	// let self=props;
    return axios({
        method: 'post',
        url: endpoint,
        data: payload
      })
      
      .then(function (response) {
        
        const { token,user} = response.data;
        //alert("output username="+user.first_name);
        axios.defaults.headers.common.Authorization = `Token ${token}`;
		
		const lan = user.language.split(',');
          user.languages=[];
          for(var i=0;i<lan.length;i++){
            var newLan={};
            newLan.key=lan[i];
            newLan.item=lan[i].charAt(0).toUpperCase() + lan[i].substr(1).toLowerCase();
            user.languages.push(newLan);
          }
          
        console.log("response aauthservice "+response);
      //  Promise.resolve(response);
     // response.data.status=response.status;
       return response;
            
         
      })
      .catch(function (error) {
       // alert(error.response.status);
        if(error.response.data.non_field_errors!=null)
        {
        alert("API call fails with error:"+error.response.data.non_field_errors);
        console.log(error.response.data.non_field_errors);
        }
        else{
        alert("API call fails with error:"+error);
        console.log(error);
        }
        return error;
        
      });
      
}
const logOut = async () => {
  //AsyncStorage.clear();
  return {
    status: "success",
    message: "You are logged out",
  };
};

const onLoad = async () => {
  const [cookies, setCookie] = useCookies(['bhaashadan']);
  const user=null;
  if(cookies.bhaashadan!='undefined' && cookies.bhaashadan!=null && cookies.bhaashadan.user!=null)
{
 // initialRouteName='UserDashboard';
   user=cookies.bhaashadan.user;
  axios.defaults.headers.common.Authorization=cookies.bhaashadan.token;
  //param={...props,user:user}
}
  return {
    status: "success",
    user: user,
  };
};

export default {
  logIn,
  logOut,
  onLoad,
};
