import React, { useState} from "react";
import { View, Text, StyleSheet, Button,TouchableOpacity,ActivityIndicator,FlatList,Modal } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "./../../actions/auth";
import { useCookies } from "react-cookie";
import { withCookies, Cookies } from 'react-cookie';
import { initialize } from "../../actions/fetch";
import { clearAllStoreData } from "../../actions/rootAction";
import Colors from "./../../constant/color"
import Styles from "./../../constant/GlobalStyles"
import ButtonTypeRadio from "./../../customComponent/ButtonTypeRadio"
import { setSelectedLanguage,setWorkType,getParagraph } from "../../actions/fetch";
import languageReducer from "../../reducers/languages";
import CardFrame from "../../customComponent/cardFrame";
import { useEffect } from "react";
import FlatListView from "../../customComponent/FlatLIstView";
import Header from "../../customComponent/Header";
import Profile from "../../customComponent/Profile";
import Logo from "../../customComponent/logo";
import ModalView from '../../customComponent/ModalView';

const Home = (props) => {
  //const [cookies, setCookie] = useCookies();
  const state = useSelector((state) => state);
  //const [workType,setWorkType]=useState("Pending");
  const [{selectedItem, modalVisible,isLoading},setState]=useState({selectedItem:null,modalVisible:false,isLoading:false});
  
  const dispatch = useDispatch();
  
  const { navigation }=props;

  
  alert("reduce "+JSON.stringify(state));
  var first_name='';
  if(state && state.auth && state.auth.user)
  first_name=state.auth.user.first_name;

  
const onLogout = (props) => {
  const {cookies}=props;
  //alert("home cookie"+JSON.stringify(cookies));
  
    dispatch(clearAllStoreData())

        navigation.replace("LoginScreen");
 
  };
 function onCardClick(item){
    if(item)
       selectedItem=item;
setState({modalVisible:!modalVisible})
}
  function onSetLanguage(state,item){
    //const dispatch = useDispatch();
    {
      if(item.key!=state.language.selectedLanguage.key) {
        var alreadyExists=false;
        alreadyExists=getAlreadyExist(state,item.key,state.language.selectedWorkType)
      dispatch(setSelectedLanguage({selectedLanguage:item,selectedWorkType:state.language.selectedWorkType,alreadyExists:alreadyExists}))
      }
    }
  };
  function getAlreadyExist(state,selectedLanguage,selectedWorkType){
    var obj=state.language.listOfObject.filter((obj)=>(obj.selectedLanguage.key==selectedLanguage.key && obj.selectedWorkType==selectedWorkType));
    alert("obj"+obj);
    if(obj.length>0)
    return true;
  return false;
  } ;
  function onSetWorkType(state,item){
   // const dispatch = useDispatch();
    {
    if(state.language.workType!=item) {
      var alreadyExists=false;
      alreadyExists=getAlreadyExist(state,state.language.selectedLanguage.key,item);
      dispatch(setWorkType({selectedLanguage:state.language.selectedLanguage,selectedWorkType:item,alreadyExists:alreadyExists}))
    }  
  }
};
 
return (
  
  <View style={Styles.container}>
    
    {
      (state.auth.user)?
      <View style={localStyles.container}>      
      <Header showSearch="false"/>
      
      <View style={localStyles.middleContainer}>
      <Logo/>
      </View>
      
      <View style={localStyles.lastContainer}>
          <View style={localStyles.Profile}>
              <Profile user={state.auth.user} {...props}/>
          </View>
          <View style={localStyles.content}>
          
              <View style={localStyles.lanConatiner}>
                  <View style={{alignContent:'flex-start',justifyContent:'flex-start',flexDirection:'row',minWidth:'120px',flexWrap:'wrap'}}>
                       { state.auth.user.languages.map((item,index) => 
                      <ButtonTypeRadio 
                      key={item.key}
                     style={item.key==state.language.selectedLanguage.key?[{...localStyles.activeButtonStyle,...localStyles.activeWorkType}]:[{...localStyles.buttonStyle,...localStyles.passiveWorkType}]} 
                     item={item} 
                     handleClick={()=>{onSetLanguage(state,item)}}
                      />)}
                  </View>
                  <View style={{alignContent:'flex-end', justifyContent:'flex-end',flexDirection:'row',minWidth:'80px'}}>
                  <TouchableOpacity onPress={()=>{onSetWorkType(state,"Uploaded")} }>
                        <Text style={[{...localStyles.toggleFirstHalf},state.language.selectedWorkType!="Uploaded"?{...localStyles.passiveWorkType}:{...localStyles.activeWorkType}]}>Uploaded</Text></TouchableOpacity>
                      <TouchableOpacity onPress={()=>{onSetWorkType(state,"Pending")} }>
                        <Text style={[{...localStyles.toggleSecondHalf},state.language.selectedWorkType!="Pending"?{...localStyles.passiveWorkType}:{...localStyles.activeWorkType}]}>Pending</Text></TouchableOpacity>
                  </View>
              
              </View>
              <View style={localStyles.tileContainer}>
              {isLoading?
              <View>
                  <ActivityIndicator size='large'/>

              </View>
              
              :
              <View>
              
           <FlatListView stateObject={state.language}/>
           </View>   }        
                </View> 
                </View>
                </View>  
                </View>
  
  :
  <>No User found</>
    }
     <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
             //   onRequestClose={this.onCardClick.bind(this)} 
                
                >
                    <ModalView 
                    item={selectedItem}
                    onPressAction={()=>onCardClick()}
                    onUploadAction={()=>updateAPICall()}
                    workType={state.language.selectedWorkType}
                    />
                </Modal>  
  
    </View>

    
  );
};
  

 
//export default withCookies(Home);
export default Home


const localStyles= StyleSheet.create({
  header:{
      backgroundColor:'#0eca7e',
      justifyContent:'center',
      //height:'10%',
      flex:1,
      width:'100%', 
     // flexGrow:1,
     minwidth:'380px',
  },
  middleContainer:{
      backgroundColor:'#0aae6c',
      justifyContent:'center',
     // height:'15%',
     flex:1.5,
      width:'100%',
      minwidth:'380px',
  },
  lastContainer:{
      flexDirection:'row',
      backgroundColor:'#e3eaea',
      justifyContent:'center',
     // height:'35%',
     flex:8.5,
      width:'100%',
      minWidth:'380px'
     // flexWrap:'wrap',
      
  },
  Profile:{
      top:'-15%',
     // width:'30%',
      height:'100%',
      flex:3,
      margin:5,
      padding:5,       
      alignItems:'center',
      alignContent:'center',
      flexDirection:'column',
      zIndex:1,
      minWidth:'120px',
      overflow:'auto',
  },
  container: {
      //height:'100%',
      width:'100%',

      flex:10,
      flexDirection:'column',
      backgroundColor: '#0eca7e',
      alignItems: 'center',
      justifyContent: 'flex-start',
      //flexWrap:'nowrap',
     // overflow:'auto',
      zIndex:0,
      minWidth:'380px'
    },
    content:{
       //height:height,
      //width:'65%',
      minWidth:'200px',
      height:'100%',
      flex:7,
       flexDirection:'column',
       backgroundColor: '#e3eaea',
       alignItems: 'flex-start',
       justifyContent: 'center',
       alignContent:'flex-start',
       //flexWrap:'wrap',
      // overflow:'auto',
       // top:100,
     },
    tileContainer:{ 
      //height:height,
      flex:9,
      width:'100%',
      height:'80%',
      flexDirection:'column',
      backgroundColor: '#e3eaea',
      alignItems: 'flex-start',
      justifyContent: 'flex-start',
      alignContent:'flex-start',
      //flexWrap:'wrap',
      //overflow:'auto',
      // top:100,
    },
    lanConatiner:{ 
      //flex:1,
      width:'100%',
     // width:'200px',
      flexDirection:'row',
      backgroundColor: '#e3eaea',
      justifyContent: 'space-between',
     // justifyContent: '//#endregion',
     // alignContent:'center',
     // flexWrap:'wrap',
      overflow:'auto',
     // top:100,
    },
    activeButtonStyle:{
      backgroundColor:Colors.dashboardRow2Color,
      borderColor:Colors.dashboardRow2Color,
      borderWidth:1,
      borderRadius:10,
      margin:5,
      height:20,
      //fontSize:10,
      paddingHorizontal:2,
    },
    
    buttonStyle:{
      backgroundColor:Colors.dashboardRow1Color,
      borderColor:Colors.dashboardRow2Color,
      borderWidth:1,
      borderRadius:10,
      margin:5,
      height:20,
      //fontSize:10,
      paddingHorizontal:2,
    },
    toggleFirstHalf:{
      backgroundColor:Colors.dashboardRow2Color,
      borderBottomStartRadius:10,
      borderTopStartRadius:10,
      borderBottomEndRadius:0,
      borderTopEndRadius:0,
      paddingHorizontal:2,
      marginVertical:5,
      height:20,
      //fontSize:10,
    },
    toggleSecondHalf:{
      backgroundColor:Colors.dashboardRow2Color,
      borderBottomStartRadius:0,
      borderTopStartRadius:0,
      borderBottomEndRadius:10,
      borderTopEndRadius:10,
      paddingHorizontal:2,
      //marginTop:5,
      marginVertical:5,
      height:20,
     // fontSize:10,
    },
    passiveWorkType:{
      fontFamily:'Roboto',
      fontSize:'14px',
      fontWeight:'500',
      fontStyle:'normal',
      lineHeight:'20px',
      backgroundColor:'white',
    },
    activeWorkType:{
      fontFamily:'Roboto',
      fontSize:'14px',
      fontWeight:'500',
      fontStyle:'normal',
      lineHeight:'20px',
    }
});
