import { setSelectedLanguage } from "../actions/fetch";
import { SETLANGUAGE,ONLOAD,LOGIN_SUCCESS, LOAD_PARA, SETWORKTYPE } from "../actions/type";
import fetchReducer from "./fetch";

import { combineReducers, configureStore } from '@reduxjs/toolkit'

//const user = null;

const initialState=['Pending','Uploaded'];
 const workTypeReducer= (state=initialState, action) => {
  const { type, payload } = action;
  switch(type) {

      case LOGIN_SUCCESS:
                return {selectedWorkType:"Pending"}
      case SETWORKTYPE:
              return {selectedWorkType:payload.selectedWorkType};
      case SETLANGUAGE:
        return {selectedWorkType:payload.selectedWorkType};
    /*  case SETLANGUAGE:
              return {payload.selectedLanguage*/
 default:
  return state;
}  
};
