import { LOGIN_SUCCESS, LOGOUT,ONLOAD } from "../actions/type";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useCookies } from 'react-cookie';
const initialState = user
  ? { isLoggedIn: true, user:user }
  : { isLoggedIn: false, user: null,languages:null };

const user = null;
const formLanguages=(payload)=>{
  if(payload && payload.user){
      const user=payload.user;
      const lan=user.languages;
      if(lan){
        var languages=[];
        var tempObj={
          id:"",
          title:"",
          Uploaded:{
          isPrevApiCalled:false,
          count:1,
          items:[],
          pagenumber:1,
          lastPageNumber:-1,
          nextPage:null,
          scrollIndex:0,
          hasMore:true
          }
          ,
          Pending:{
          isPrevApiCalled:false,
          count:1,
          items:[],
          pagenumber:1,
          lastPageNumber:-1,
          nextPage:null,
          scrollIndex:0,
          hasMore:true
          }
          
          }
        for(var i=0;i<lan.length;i++)
        {
          tempObj.id=lan[i].key;
          tempObj.title=lan[i].item;
          languages.push(tempObj);

        }
        return languages;
      }

  }
  return null;
}

 const auth= (state = initialState, action) => {
  const { type, payload } = action;
  const languages=formLanguages(payload);
switch (type) {
  case ONLOAD:
    return{
      ...state,
      isLoggedIn: payload.user?true:false,
      user: payload.user,  
      languages:languages,
    };
  case LOGIN_SUCCESS:
      return {
        ...state,
        isLoggedIn: true,
        user: payload.user,
        languages:languages,
      };
  case LOGOUT:
      return {
        ...state,
        isLoggedIn: false,
        user: null,
      };
    default:
      return state;
  }
};
export default auth
