import React, { useState} from "react";
import { View, Text, StyleSheet, Button,TouchableOpacity,ActivityIndicator,FlatList } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "./../../actions/auth";
import { useCookies } from "react-cookie";
import { withCookies, Cookies } from 'react-cookie';
import { initialize } from "../../actions/fetch";
import { clearAllStoreData } from "../../actions/rootAction";
import Colors from "./../../constant/color"
import Styles from "./../../constant/GlobalStyles"
import ButtonTypeRadio from "./../../customComponent/ButtonTypeRadio"
import { setSelectedLanguage,setWorkType,getParagraph } from "../../actions/fetch";
import languageReducer from "../../reducers/languages";
import CardFrame from "../../customComponent/cardFrame";
import { useEffect } from "react";
import FlatListView from "../../customComponent/FlatLIstView";

const Home = (props) => {
  //const [cookies, setCookie] = useCookies();
  const state = useSelector((state) => state);
  //const [workType,setWorkType]=useState("Pending");
  const [isLoading,setIsLoading]=useState(false);
  
  //const [tempIndex,setSelectedLanguage]=useState(0);
  //setSelectedLanguage(0);
  const dispatch = useDispatch();
  //dispatch(getParagraph({selectedLanguageKey:state.language.selectedLanguage.key,workType:state.language.selectedWorkType}))
  /*
  useEffect(()=>
  dispatch(getParagraph({selectedLanguageKey:state.language.selectedLanguage.key,workType:state.language.selectedWorkType}))
  .then((response)=>{
      setIsLoading(false)
    }),[]);*/
  const { navigation }=props;

  
  alert("reduce "+JSON.stringify(state));
  var first_name='';
  if(state && state.auth && state.auth.user)
  first_name=state.auth.user.first_name;

  
const onLogout = (props) => {
  const {cookies}=props;
  //alert("home cookie"+JSON.stringify(cookies));
  
    dispatch(clearAllStoreData())

        navigation.replace("LoginScreen");
 
  };
 
return (
  
  <View style={Styles.container}>
    {
      (state.auth.user)?
      <View style={localStyles.container}>      
      {/*<Header showSearch="false">*/}
      <View style={localStyles.middleContainer}>
      </View>
      <View style={localStyles.lastContainer}>
          <View style={localStyles.Profile}>
              {/*<Profile user={this.propsSourceObject.user} {...this.props}/>*/}
          </View>
          <View style={localStyles.content}>
          
              <View style={localStyles.lanConatiner}>
                  <View style={{alignContent:'flex-start',justifyContent:'flex-start',flexDirection:'row',minWidth:'120px',flexWrap:'wrap'}}>
                       { state.auth.user.languages.map((item,index) => 
                      <ButtonTypeRadio 
                      key={item.key}
                     style={item.key==state.language.selectedLanguage.key?[{...localStyles.activeButtonStyle,...localStyles.activeWorkType}]:[{...localStyles.buttonStyle,...localStyles.passiveWorkType}]} 
                     item={item} 
                     handleClick={()=>
                      {if(item.key!=state.language.selectedLanguage.key) dispatch(setSelectedLanguage({selectedLanguage:item,selectedWorkType:"Pending"}))}}
                      />)}
                  </View>
                  <View style={{alignContent:'flex-end', justifyContent:'flex-end',flexDirection:'row',minWidth:'80px'}}>
                      <TouchableOpacity onPress={()=>{if(state.language.workType!="Uploaded") dispatch(setWorkType({workType:"Uploaded"}))}} ><Text style={[{...localStyles.toggleFirstHalf},state.language.selectedWorkType!="Uploaded"?{...localStyles.passiveWorkType}:{...localStyles.activeWorkType}]}>Uploaded</Text></TouchableOpacity>
                      <TouchableOpacity onPress={()=>{if(state.language.workType!="Pending") dispatch(setWorkType({workType:"Pending"}))}} ><Text style={[{...localStyles.toggleSecondHalf},state.language.selectedWorkType!="Pending"?{...localStyles.passiveWorkType}:{...localStyles.activeWorkType}]}>Pending</Text></TouchableOpacity>
                  </View>
              
              </View>
              <View style={localStyles.tileContainer}>
              {isLoading?
              <View>
                  <ActivityIndicator size='large'/>
                  {
                  //dispatch(getParagraph({selectedLanguageKey:state.language.selectedLanguage.key,workType:state.language.selectedWorkType})).then((response)=>{
                  //  setIsLoading(false)
                //  })
                
                
                  
                                  
                  }
              </View>
              :
              <View>
           
           <FlatListView
                       //ref={this.flatListRef}
                       keyExtractor={(item) => item.id}
                       key={(item)=>item.id}
                       contentContainerStyle={{ justifyContent: 'center', flexDirection: 'row', flexWrap: 'wrap' }}
                       data={state.language.finalObject[state.language.selectedLanguage.key][state.language.selectedWorkType].results}
                       renderItem={(item)=><CardFrame key={item.id} {...item} workType={state.language.selectedWorkType} onPress={()=>{alert(JSON.stringify(item))}}/>}
                    //  renderItem={(item)=><View><Text>{item.id}</Text></View>}
                      ListEmptyComponent={<View><Text> no item to render</Text></View>}
                     // hasMore={state.language.finalObject[state.language.selectedLanguage.key][state.language.selectedWorkType].hasMore}
                      /*onEndReached={
                        state.language.finalObject[state.language.selectedLanguage.key][state.language.selectedWorkType].hasMore?
                        handleScroll.bind(this)
                       :
                       ""
                      }
                      onEndReachedThreshold={
                        this.state.itemToRender[state.language.selectedLanguage.key][this.state.language.selectedWorkType].hasMore?
                       0.2
                       :
                       0
                       }
                       */
                       //refreshing={refreshing}
                       horizontal={false}
                       //onEndReached={this.handleScroll.bind(this)}
                      // refreshing={this.state.refreshing}
                    //  getItemLayout={(any,number) => ( {length: 200,offset:(250*((number-1)/numberOfColumn)), index: number})}
                       //initialScrollIndex={0}
                     //  initialScrollIndex={this.state.itemToRender[this.state.workType][this.state.selectedLanguage.key].scrollIndex}
                       //scrollEnabled={true}
                      // onScroll={this.handleScroll1.bind(this)}
                      // maxToRenderPerBatch={10}
                      // numColumns={2}
                      // style={{width:'100%',flexGrow:1}}
                       //onMomentumScrollBegin={() => {this.onEndReachedCalledDuringMomentum = false;}}
                       />
             </View>
              }
          </View>  
          </View>
     </View>
            
  </View>
  
  :
  <>No User found</>
    }
    </View>
    
  );
};
export default Home;
const localStyles= StyleSheet.create({
  header:{
      backgroundColor:'#0eca7e',
      justifyContent:'center',
      //height:'10%',
      flex:1,
      width:'100%', 
     // flexGrow:1,
     minwidth:'380px',
  },
  middleContainer:{
      backgroundColor:'#0aae6c',
      justifyContent:'center',
     // height:'15%',
     flex:1.5,
      width:'100%',
      minwidth:'380px',
  },
  lastContainer:{
      flexDirection:'row',
      backgroundColor:'#e3eaea',
      justifyContent:'center',
     // height:'35%',
     flex:8.5,
      width:'100%',
      minWidth:'380px'
     // flexWrap:'wrap',
      
  },
  Profile:{
      top:'-15%',
     // width:'30%',
      height:'100%',
      flex:3,
      margin:5,
      padding:5,       
      alignItems:'center',
      alignContent:'center',
      flexDirection:'column',
      zIndex:1,
      minWidth:'120px',
      overflow:'auto',
  },
  container: {
      //height:'100%',
      width:'100%',

      flex:10,
      flexDirection:'column',
      backgroundColor: '#0eca7e',
      alignItems: 'center',
      justifyContent: 'flex-start',
      //flexWrap:'nowrap',
     // overflow:'auto',
      zIndex:0,
      minWidth:'380px'
    },
    content:{
       //height:height,
      //width:'65%',
      minWidth:'200px',
      height:'100%',
      flex:7,
       flexDirection:'column',
       backgroundColor: '#e3eaea',
       alignItems: 'flex-start',
       justifyContent: 'center',
       alignContent:'flex-start',
       //flexWrap:'wrap',
      // overflow:'auto',
       // top:100,
     },
    tileContainer:{ 
      //height:height,
      flex:9,
      width:'100%',
      height:'80%',
      flexDirection:'column',
      backgroundColor: '#e3eaea',
      alignItems: 'flex-start',
      justifyContent: 'flex-start',
      alignContent:'flex-start',
      //flexWrap:'wrap',
      //overflow:'auto',
      // top:100,
    },
    lanConatiner:{ 
      //flex:1,
      width:'100%',
     // width:'200px',
      flexDirection:'row',
      backgroundColor: '#e3eaea',
      justifyContent: 'space-between',
     // justifyContent: '//#endregion',
     // alignContent:'center',
     // flexWrap:'wrap',
      overflow:'auto',
     // top:100,
    },
    activeButtonStyle:{
      backgroundColor:Colors.dashboardRow2Color,
      borderColor:Colors.dashboardRow2Color,
      borderWidth:1,
      borderRadius:10,
      margin:5,
      height:20,
      //fontSize:10,
      paddingHorizontal:2,
    },
    
    buttonStyle:{
      backgroundColor:Colors.dashboardRow1Color,
      borderColor:Colors.dashboardRow2Color,
      borderWidth:1,
      borderRadius:10,
      margin:5,
      height:20,
      //fontSize:10,
      paddingHorizontal:2,
    },
    toggleFirstHalf:{
      backgroundColor:Colors.dashboardRow2Color,
      borderBottomStartRadius:10,
      borderTopStartRadius:10,
      borderBottomEndRadius:0,
      borderTopEndRadius:0,
      paddingHorizontal:2,
      marginVertical:5,
      height:20,
      //fontSize:10,
    },
    toggleSecondHalf:{
      backgroundColor:Colors.dashboardRow2Color,
      borderBottomStartRadius:0,
      borderTopStartRadius:0,
      borderBottomEndRadius:10,
      borderTopEndRadius:10,
      paddingHorizontal:2,
      //marginTop:5,
      marginVertical:5,
      height:20,
     // fontSize:10,
    },
    passiveWorkType:{
      fontFamily:'Roboto',
      fontSize:'14px',
      fontWeight:'500',
      fontStyle:'normal',
      lineHeight:'20px',
      backgroundColor:'white',
    },
    activeWorkType:{
      fontFamily:'Roboto',
      fontSize:'14px',
      fontWeight:'500',
      fontStyle:'normal',
      lineHeight:'20px',
    }
});
