import { SETLANGUAGE,ONLOAD,LOGIN_SUCCESS, LOAD_PARA, SETWORKTYPE } from "../actions/type";
import fetchReducer from "./fetch";
import workTypeReducer from "./worktype";
import { combineReducers, configureStore } from '@reduxjs/toolkit'

//const user = null;

const initialState={languages:[]}
 const dataReducer= (state, action) => {
  const { type, payload } = action;
  //alert(type+" state in language reducer "+JSON.stringify(state));
 /* if(payload )
  alert(type+" language payload next url"+JSON.stringify(payload)+" prev state ");//+state.selectedObject.nextUrl);
else
  alert(type+" language payload data not exist");
*/
switch (type) {
  case SETLANGUAGE:
      return{
        ...state,
		selectedLanguage:payload.selectedLanguage,
		selectedWorkType:payload.selectedWorkType,
    [payload.selectedWorkType]:state[payload.selectedLanguage.key] && state[payload.selectedLanguage.key][payload.selectedWorkType]
									?
									{...state[payload.selectedLanguage.key][payload.selectedWorkType],
                    results:[...state[payload.selectedLanguage.key][payload.selectedWorkType].results,payload.results],
                    nextUrl:payload.data.next,prevUrl:payload.data.previous}
									:
									{
										results:[payload.results],nextUrl:payload.data.next,prevUrl:payload.data.previous
									}

                    }
    case SETWORKTYPE:
      return{
         ...state,
		selectedLanguage:payload.selectedLanguage,
		selectedWorkType:payload.selectedWorkType,
      [payload.selectedWorkType]:state[payload.selectedLanguage.key] && state[payload.selectedLanguage.key][payload.selectedWorkType]
									?
									{...state[payload.selectedLanguage.key][payload.selectedWorkType],results:[...state[payload.selectedLanguage.key][payload.selectedWorkType].results,payload.results],nextUrl:payload.data.next,prevUrl:payload.data.previous}
									:
									{
										results:[payload.results],nextUrl:payload.data.next,prevUrl:payload.data.previous
									}

                    }
                    
                    
    case LOAD_PARA:
      return{
        ...state,
		selectedLanguage:payload.selectedLanguage,
		selectedWorkType:payload.selectedWorkType,
      [payload.selectedWorkType]:state[payload.selectedLanguage.key] && state[payload.selectedLanguage.key][payload.selectedWorkType]
									?
									{...state[payload.selectedLanguage.key][payload.selectedWorkType],results:[...state[payload.selectedLanguage.key][payload.selectedWorkType].results,payload.results],nextUrl:payload.data.next,prevUrl:payload.data.previous}
									:
									{
										results:[payload.results],nextUrl:payload.data.next,prevUrl:payload.data.previous
									}

                    }
        
        
        
                    
                      
       //selectedObject:{...state.selectedObject,results:['a','b','c']}
      
    case LOGIN_SUCCESS:
                  return {
                    ...state,
                  //  selectedLanguage:payload.user.languages[0],
                  //  selectedWorkType:"Pending",

                    Pending:{selectedLanguage:payload.user.languages[0],
                              selectedWorkType:"Pending",
                              results:['1']
                            }}
                    
                  
                
                  
    default:
      return state;
  }
};
  

function languageReducer(state, action) {
    const intermediateState = dataReducer(state, action)
    const intermediateState2 = workTypeReducer(intermediateState, action)
    //const finalState = fetchReducer(intermediateState2, action)
    return intermediateState2
  }
  
export default languageReducer
