import workTypeReducer from "./worktype";
import { combineReducers, configureStore } from '@reduxjs/toolkit'

//const user = null;

const initialState={selectedWorkType:"Pending"}
 const dataReducer= (state=initialState, action) => {
  const { type, payload } = action;
  //alert(type+" state in language reducer "+JSON.stringify(state));
  if(payload && payload.data)
  alert(type+" payload next url"+payload.data.next+" prev state "+state.selectedObject.nextUrl);
else
  alert(tyepe+" payload data not exist");
switch (type) {
  case SETLANGUAGE:
      return{
        ...state,
        selectedLanguage:payload.selectedLanguage,
       // selectedWorkType:payload.selectedWorkType,
        selectedObject:payload.alreadyExists?
                       state.listOfObject.filter((obj)=>(obj.selectedLanguage.key==payload.selectedLanguage.key && obj.selectedWorkType==payload.selectedWorkType))
                       :{selectedLanguage:payload.selectedLanguage,
                        selectedWorkType:payload.selectedWorkType,
                        results:payload.data.results,
                        prevUrl:payload.url,
                        nextUrl:payload.data.next    
                      },
      };
    case SETWORKTYPE:
      return{
        ...state,
      //  selectedLanguage:payload.selectedLanguage,
        selectedWorkType:payload.selectedWorkType,
        selectedObject:payload.alreadyExists?
                       state.listOfObject.filter((obj)=>(obj.selectedLanguage.key==payload.selectedLanguage.key && obj.selectedWorkType==payload.selectedWorkType))
                       :{selectedLanguage:payload.selectedLanguage,
                        selectedWorkType:payload.selectedWorkType,
                        results:[...state.selectedObject.results,payload.data.results],
                        prevUrl:state.selectedObject.nextUrl,
                        nextUrl:payload.data.next
                      },
        
      }
    case LOAD_PARA:
      return{
        ...state,
        selectedObject:{
                        //...state.selectedObject,
                        selectedLanguage:state.selectedLanguage,
                        selectedWorkType:state.selectedWorkType,
                        results:[...state.selectedObject.results,...payload.data.results],
                       // results:[payload.data.results],
                        prevUrl:payload.url,
                        nextUrl:payload.data.next
                        }
                        
       //selectedObject:{...state.selectedObject,results:['a','b','c']}
      }
    case LOGIN_SUCCESS:
                  return {
                    ...state,
                    selectedLanguage:payload.user.languages[0],
                    selectedWorkType:"Pending",
                    selectedObject:{selectedLanguage:payload.user.languages[0],selectedWorkType:"Pending",results:[],
                    prevUrl:payload.url,
                    nextUrl:null},
                    
                  }
    default:
      return state;
  }
  /*case ONLOAD:
