import languageReducer from "./languages";

//const user = null;
//const initialState={lanIndex:0,workType:"Pending",results:[]}
 const dataReducer= (state, action) => {
  const { type, payload } = action;
  
switch (type) {
  case ONLOAD:
    return{
      ...state,
     // selectedObject:{...state.selectedObject,workType:state.selectedWorkType},
    // trialObject:{[payload.user.languages[0]]:{[state.selectedWorkType]:{results:[]}}},
    // finalObject:{...state.finalObject,[payload.user.languages[0]]:{[state.selectedWorkType]:{results:[]}}}
     // language:{lanIndex:0,workType:"Pending",results:[]}
     
    };
  case SETLANGUAGE:
    alert("state in worktype reducer "+JSON.stringify(state));
    return{
      ...state, 
      //languagelanIndex:payload.lanIndex,
     // workType:payload.workType,
     //languageList:[...state.languagList.filter((lan)=>(!(lan.lanIndex==payload.lanIndex && lan.workType==payload.workType) )),payload],
      
     //selectedObject:{...state.selectedObject,workType:state.selectedWorkType},
    trialObject:{[state.trialObject]:{[state.selectedWorkType]:""}},
     //finalObject:{...state.finalObject,...state.trialObject}}

      
      
     // results:state.results,
     // results:null
      //results:new Set(payload.results)
    };
    default:
      return state;
  }
};

function workTypeReducer(state, action) {
    const intermediateState =dataReducer(state, action)
    const finalState =fetchReducer(intermediateState, action)
    return finalState
  }



export default workTypeReducer
