import { SETLANGUAGE } from "../actions/type";
import fetchReducer from "./fetch";
import workTypeReducer from "./worktype";
import { combineReducers, configureStore } from '@reduxjs/toolkit'

//const user = null;

const initialState={languageList:[{lanIndex:0,workType:"Pending",results:[]}]}
 const languageReducer= (state=initialState, action) => {
  const { type, payload } = action;
  
switch (type) {
  case SETLANGUAGE:
    alert("state in language reducer "+JSON.stringify(state));
    return{
      ...state, 
      languageList:[state.languageList.filter((lan)=>(lan.lanIndex!=payload.lanIndex ||lan.workType!=payload.workType )),payload],
      selectedLanguage:payload
    };
    default:
      return state;
  }
};
/*
function languageReducer(state, action) {
    const intermediateState = workTypeReducer(state, action)
    const finalState = dataReducer(intermediateState, action)
    return finalState
  }
  */
export default languageReducer
