import { SETLANGUAGE } from "../actions/type";
import fetchReducer from "./fetch";
import workTypeReducer from "./worktype";
import { combineReducers, configureStore } from '@reduxjs/toolkit'

//const user = null;

const initialState={selectedLanguage:0,selectedWorkType:"Pending",languageList:[{languageIndex:0,workType:"Pending",results:[]}]}
 const dataReducer= (state=initialState, action) => {
  const { type, payload } = action;
  
switch (type) {
  case SETLANGUAGE:
    alert("state in language reducer "+JSON.stringify(state));
    return{
      ...state, 
     
      languageList:[...state.languageList.filter((lan)=>(!(lan.lanIndex==payload.lanIndex && lan.workType==payload.workType) ))],
      selectedLanguage:payload.lanIndex,
      selectedWorkType:payload.workType,
      selectedObject:{languageIndex:payload.lanIndex},
      trialObject:{[state.selectedLanguage]:""},
      //finalObject:{...state.finalObject,...state.trialObject}
     //laguagepayload:payload
     // {lanIndex:state.lanIndex,workType:state.workType,results:state.results}],
     // languageElement:null,
      //selectedLanguage:payload
    };
    default:
      return state;
  }
};

function languageReducer(state, action) {
    const intermediateState = dataReducer(state, action)
    const finalState = workTypeReducer(intermediateState, action)
    return finalState
  }
  
export default languageReducer
