import { LOAD_JOB,LOAD_PARA } from "../actions/type";

import { useCookies } from 'react-cookie';
import globalconst from "../constant/globalvariables";

//const user = null;

const initialState = {languages:[]}

const formLanguages=(user)=>{
  //if(payload && user)
  {
      //const user=user;
      const lan=user.languages;
      if(lan){
        var languages=[];
        
        for(var i=0;i<lan.length;i++)
        {
          var key=lan[i].key;
          var item=lan[i].item;
          if(key==="bengali"){
            key="bangla";
            item="Bangla";
          }
          var tempObj={
            id:key,
            title:item,
            Uploaded:{
            isPrevApiCalled:false,
            count:-1,
            items:[],
            pagenumber:0,
            lastPageNumber:-1,
            nextPage:globalconst.apiBaseUrl+'api/job?language='+key+'&page=1',
            scrollIndex:0,
            hasMore:true
            }
            ,
            Pending:{
            isPrevApiCalled:false,
            count:-1,
            items:[],
            pagenumber:0,
            lastPageNumber:-1,
            nextPage:globalconst.apiBaseUrl+'api/paragraph?language='+key+'&page=1',
            scrollIndex:0,
            hasMore:true
            }
            
            }
          
          languages.push(tempObj);

        }
        return languages;
      }

  }
  return null;
}

 const fetchReducer= (state = initialState, action) => {
  const { type, payload } = action;
  
switch (type) {
  case LOAD_JOB:
    return{
      ...state, 
      languages:languages,
    };
  case LOAD_PARA:
      return {
        ...state,
        languages:languages,
      };
  case INITIALIZE:
    const languages=formLanguages(payload);
      return {
        ...state,
        languages:languages,
      };
    default:
      return state;
  }
};
export default fetchReducer
