import { LOGIN_SUCCESS, LOGOUT,ONLOAD } from "../actions/type";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useCookies } from 'react-cookie';
const initialState = user
  ? { isLoggedIn: true, user }
  : { isLoggedIn: false, user: null };

const user = null;
function initializeUser(){
  const [cookies, setCookie] = useCookies(['bhaashadan']);
 user= cookies.bhaashadan?(cookies.bhaashadan.user?cookies.bhaashadan.user:null):null
}

 const auth= (state = initialState, action) => {
  const { type, payload } = action;
switch (type) {
  case ONLOAD:
    return{
      ...state,
      isLoggedIn: true,
      user: payload.user,  
    }
    case LOGIN_SUCCESS:
      return {
        ...state,
        isLoggedIn: true,
        user: payload.user,
      };
case LOGOUT:
      return {
        ...state,
        isLoggedIn: false,
        user: null,
      };
    default:
      return state;
  }
};
export default auth
