import { SETLANGUAGE } from "../actions/type";
import fetchReducer from "./fetch";
import workTypeReducer from "./worktype";
import { combineReducers, configureStore } from '@reduxjs/toolkit'

//const user = null;

//const initialState={selectedLanguageIndex:0}
 const dataReducer= (state, action) => {
  const { type, payload } = action;
  
switch (type) {
  case SETLANGUAGE:
    return{
      ...state, 
      selectedLanguageIndex:payload.index,
      
    };
    default:
      return state;
  }
};
function languageReducer(state, action) {
    //const intermediateState = combinedReducer(state, action)
    const finalState = dataReducer(intermediateState, action)
    return finalState
  }
export default languageReducer
